    private Integer changePriceFormat(String price) {
       String[] arrPrice = price.split(" ", 5);
       String newPrice = arrPrice[1].replace(",-","");
       newPrice = newPrice.replace(".","");
       
       return Integer.parseInt(newPrice);
    }
    
    private String toRupiah(String price) {
       int newPrice = Integer.parseInt(price); 
       NumberFormat nf = NumberFormat.getNumberInstance(new Locale("in", "ID"));
       
       return nf.format(newPrice);
    }
    
    private String toRupiahWithRP(String price) {
        int newPrice = Integer.parseInt(price);
        Locale localeID = new Locale("in", "ID");
        NumberFormat formatRupiah = NumberFormat.getCurrencyInstance(localeID);
        return formatRupiah.format(newPrice);
    }
    
    private boolean isInteger(String text) {
       return Pattern.matches("\\d*", text);
    }
    
    private String deleteDot(String text) {
        if(text.contains(".")){
            text = text.replace(".", "");
        }
        
        return text;
    }
    
    private void qty_menu1KeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_qty_menu1KeyReleased
        if(!qty_menu1.getText().isEmpty() && this.isInteger(qty_menu1.getText())){
            int harga1 = this.changePriceFormat(harga_menu1.getText());
            String qty = qty_menu1.getText();
            int sum = Integer.parseInt(qty) * harga1;
            String result = this.toRupiah(Integer.toString(sum));
        
            sum_menu1.setText(result);
        } else {
            sum_menu1.setText("");
        }
    }//GEN-LAST:event_qty_menu1KeyReleased

