    private void btnCekActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCekActionPerformed
       String sumMenu1 = sum_menu1.getText();
       String sumMenu2 = sum_menu2.getText();
       String sumMenu3 = sum_menu3.getText();
       String sumMenu4 = sum_menu4.getText();
       String sumMenu5 = sum_menu5.getText();
       String sumMenu6 = sum_menu6.getText();
       String sumMenu7 = sum_menu7.getText();
       String sumMenu8 = sum_menu8.getText();
       
       String[] allSum = {sumMenu1,sumMenu2,sumMenu3,sumMenu4,sumMenu5,sumMenu6,sumMenu7,sumMenu8};
       int sumTmp = 0;
       
       for(String i : allSum) {
           if(!i.isEmpty()){
               i = this.deleteDot(i);
               sumTmp += Integer.parseInt(i);
           }
       }
       
       String total = Integer.toString(sumTmp);   
       if(!total.equals("0")) {
            total_harga.setText(this.toRupiahWithRP(Integer.toString(sumTmp)));
       } else {
            total_harga.setText(Integer.toString(sumTmp));
       }
    }//GEN-LAST:event_btnCekActionPerformed

    private void field_pembayaranFocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_field_pembayaranFocusGained
        if(field_pembayaran.getText().equals("Inputkan uang anda")){
            field_pembayaran.setText("");
            field_pembayaran.setForeground(Color.black);
        }
    }//GEN-LAST:event_field_pembayaranFocusGained

    private void field_pembayaranFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_field_pembayaranFocusLost
        if(field_pembayaran.getText().equals("")){
            field_pembayaran.setText("Inputkan uang anda");
            field_pembayaran.setForeground(new Color(153,153,153));
        }
    }//GEN-LAST:event_field_pembayaranFocusLost

    private void field_pembayaranKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_field_pembayaranKeyReleased
        String bayar = this.deleteDot(field_pembayaran.getText());
        if(!bayar.isEmpty() && this.isInteger(bayar)){
            field_pembayaran.setText(this.toRupiah(bayar));
        } else {
            field_pembayaran.setText("");
        }
    }//GEN-LAST:event_field_pembayaranKeyReleased

    private void btnCountActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCountActionPerformed
        String total = this.deleteDot(total_harga.getText().replace("Rp", ""));
        total = total.replace(",00", "");
        
        String uang = this.deleteDot(field_pembayaran.getText());
        if(Integer.parseInt(uang) >= Integer.parseInt(total)) {
            error.setText("");
            int kembalian = Integer.parseInt(uang) - Integer.parseInt(total);
            if(kembalian != 0){
                field_kembalian.setText(this.toRupiahWithRP(Integer.toString(kembalian)));
            } else {
                field_kembalian.setText("0");
            }
        } else {
            field_kembalian.setText("0");
            error.setText("Uang tidak cukup!");
        }
    }//GEN-LAST:event_btnCountActionPerformed

